INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2013-09-26 14:35:34', '2013-09-26 14:35:34', '欢迎使用WordPress。这是系统自动生成的演示文章。编辑或者删除它，然后开始您的博客！', '世界，你好！', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2013-09-26 14:35:34', '2013-09-26 14:35:34', '', 0, 'http://wlantel.com/?p=1', 0, 'post', '', 1),
(2, 1, '2013-09-26 14:35:34', '2013-09-26 06:35:34', '这是示范页面。页面和博客文章不同，它的位置是固定的，通常会在站点导航栏显示。很多用户都创建一个“关于”页面，向访客介绍自己。例如，个人博客通常有类似这样的介绍：\n\n<blockquote>欢迎！我白天是个邮递员，晚上就是个有抱负的演员。这是我的博客。我住在天朝的帝都，有条叫做杰克的狗。</blockquote>\n\n……公司博客可以这样写：\n\n<blockquote>XYZ Doohickey公司成立于1971年，自从建立以来，我们一直向社会贡献着优秀doohicky。我们的公司总部位于天朝魔都，有着超过两千名员工，对魔都政府税收有着巨大贡献。</blockquote>\n\n您可以访问<a href="http://wlantel.com/wp-admin/">仪表盘</a>，删除本页面，然后添加您自己的内容。祝您使用愉快！', '解决方案', '', 'publish', 'closed', 'closed', '', 'solutions', '', '', '2013-10-23 01:25:52', '2013-10-22 17:25:52', '', 0, 'http://wlantel.com/?page_id=2', 0, 'page', '', 0),
(4, 1, '2013-10-23 00:58:08', '2013-10-22 16:58:08', '', 'QQ图片20130918172449', '', 'inherit', 'open', 'open', '', 'qq%e5%9b%be%e7%89%8720130918172449', '', '', '2013-10-23 00:58:08', '2013-10-22 16:58:08', '', 0, 'http://wlantel.com/wp-content/uploads/2013/10/QQ图片20130918172449.jpg', 0, 'attachment', 'image/jpeg', 0),
